import {  ACTIONS } from "./../actions/actions.js";
import { produce } from "imer";

const deck = {
    track : {
        title : "",
        bpm : 0,
        quality : "",
        duration : "",
        source : "",
        id: "",
        thumbnail : {}
    }, // 
    playBackState : {
        ready: false,
        paused: true,
        cuePoint: 0,
        cueActive: false,
        pitch: 0,
        timeLeft : null
    }
}

const chanel = {
    volume: 100,
    low: 100,
    mid : 100,
    high : 100,
    gain: 100
}

const initState = {
    chanel: {
        A:{
            ...deck
        },
        B:{
            ...deck
        }
    },
   
    mikser : {
        chanelA : {
            ...chanel,
        },
        chanelB : {
            ...chanel,
        },
        fader : {
            position : 50 //A: 0 <-> B:100
        }
    }
}

function consoleReducer(state = initState, action){
    switch(action.type){
        case ACTIONS.LOAD_TRACK : {
            /*let destination = action.destination;
            let track = action.track;
            let newState = {...state};

            newState.chanel[destination].track = {

            }*/
            const nextTrack = action.track;
            return produce(state, draftState => {
                draftState[action.destination].track = {
                    title : nextTrack.title,
                    bmp : nextTrack.bpm || 0,
                    quality : nextTrack.quality,
                    duration : nextTrack.duration,
                    source : nextTrack.source,
                    id : nextTrack.id,
                    thumbnail : nextTrack.thumbnail,
                }
            })

           // return newState;
        }

        case ACTIONS.SET_READY : {
            return nextPlayBackState(state, action.destination, false, { ready : action.value })
        }

        case ACTIONS.SET_PITCH : {
            return nextPlayBackState(state, action.destination, false, { pitch : action.pitch })
        }

        case ACTIONS.TOGGLE_PLAY : {
            const prevPause = state.chanel[action.destination]?.playBackState?.paused || true;
            return nextPlayBackState(state, action.destination, true, { paused : !prevPause })
        }

        case ACTIONS.TOGGLE_CUE : {
            const prevCue = state.chanel[action.destination]?.playBackState?.cueActive || false;
            return nextPlayBackState(state, action.destination, true, { cueActive : !prevCue })
        }

        case ACTIONS.SET_CUE_POINT : {
            return nextPlayBackState(state, action.destination, true, { cuePoint : action.position })
        }

        default : return state;
    }
}

function nextPlayBackState(state, destination, haveToBeReady, variables){
    if(!state.chanel[destination]) return state;
    if(haveToBeReady && !state.chanel[destination].playBackState.ready) return state;

    const nextState = produce(state, (draftState) => {
        for(let [variable, value] of Object.entries(variables)){
            draftState.chanel[destination].playBackState[variable] = value;
        }
    })
    return nextState;
}

export default consoleReducer;
