import React, {useCallback, useMemo} from 'react';
import {Portal} from "react-portal";
import icons from "./emoji.json";
const iconsOnPanel = 30;

const EmotiContainer = ({onSelect, node}) => {
    const createSection = useCallback(()=>{
        let section  = document.createElement('section');
        section.classList.add('emoticons-panel');
        return section;
    },[])

    const iconsPanels = useMemo(()=>{
        let panels = [];
        let section = createSection();
        icons.forEach((icon, index) => {
            let iconBtn = document.createElement('button');
            iconBtn.title = icon.description;
            iconBtn.addEventListener('click', onSelect(icon.emoji));
            section.appendChild(iconBtn);
            if(index % iconsOnPanel === 0){
                panels.push(section);
                section = createSection();
            }
        })

        return panels;
    })

    console.log("render emoti")
    return (
        <Portal node={node}>
            <div className="emoticons-container">
                {/* {iconsPanels} */}
            </div>
        </Portal>
    )
}

export default EmotiContainer;
