import store from "./../store";

let console;
export default class Console{
    constructor(){
        store.subscribe(this.handleChange())
    }

    static Create(){
        if(!console){
            console = new Console();
            return console;
        } else {
            return console;
        }
    }

    handleChange(){
        console.log(store);
    }

}
