import React, { useEffect, useState } from "react";
import Console from "./../../../../core/console/console";
import "./sync-bar.scss";

class SyncBar extends React.Component {
  constructor() {
    super(...arguments);
    this.channelInterface = Console.Get().getChannelInterface(this.props.name);
  }

  state = {
    position: 0
  };

  update() {
    let position = this.channelInterface.getSyncBarPosition();
    console.log(position);

    if (!this.props.master) {
      window.requestAnimationFrame(this.update.bind(this));
    }
  }

  render() {
    const style = {};
    style.display = this.props.master ? "none" : "block";
    if (!this.master) {
      window.requestAnimationFrame(this.update.bind(this));
    }

    return (
      <div
        className={this.props.className + " sync-bar-deck-" + this.props.name}
      >
        <div className="bar-area">
          <div className="bar-thumb" style={style} />
        </div>
      </div>
    );
  }
}

export default SyncBar;
