import React from "react";
import Console from "./../../../../../core/console/console"
import "./sync-bar.scss";

class SyncBar extends React.Component {
  constructor() {
    super(...arguments);
    this.channelInterface = Console.Get().getChannelInterface(this.props.name);
    this.thumbElement = React.createRef();

    this.lastCall = false;
    this.break = true;
  }

  state = {
    position: 0
  };

  update() {
   // console.log("update", this.props.name);
    this.setState({
      ...this.state,
      position: this.channelInterface.getSyncBarPosition()
    });

    if (!this.props.active) {
      console.log("stop interval");
      clearInterval(this.intervalHandler);
      this.intervalHandler = null;
    }
  }

  applyStyle(position){
    const style = {};
    style.display = "none";


    if (this.props.active) {
      style.display = "block";
      style.width = Math.abs(this.state.position * 100) + "%"; //max 50 %;
      if (this.state.position > 0) {
        style.left = "50%";
      } else {
        style.left = "auto";
        style.right = "50%";
      }

    }
  }

  componentDidUpdate(){
    if(this.props.active){
      this.thumbElement.style.opacity = "1";
      this.break = false;
      this.update();
    } else {
      this.thumbElement.style.opacity = "0";
      this.break = true;
    }
  }


  render() {
    return (
      <div className={this.props.className + " sync-bar-deck-" + this.props.name}>
        <div className="bar-area">
          <div ref={this.thumbElement} className="bar-thumb" />
        </div>
      </div>
    );
  }
}

export default SyncBar;
