import React, {useState, useEffect}  from "react";
import {Form} from "react-bootstrap"
import "./record-details.scss";

const RecordDetails = ({record}) => {
    const [editMode, setEditMode] = useState(false);

    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [generes, setGeneres] = useState([]);
    
    const [aviableGeneres, setAviableGeneres] = useState();

    useEffect(()=>{
        if(!record) return;

        setTitle(record.title || "")
        setDescription(record.description || "")
        setGeneres(record.generes || ["Techno", "Minimal", "Soul"]);
    },[record, setTitle, setDescription, setGeneres])

    return (
        <div className="record-details">
            <Form.Group className="record-title">
                <Form.Label>Title: </Form.Label>
                <Form.Control type="text" value={title} onChange={setTitle} disabled={!editMode}/>
            </Form.Group>
            <Form.Group className="record-description">
                <Form.Label>Description:</Form.Label>
                <Form.Control type='text' value={description} onChange={setDescription} disabled={!editMode}/>
            </Form.Group>
            <Form.Group className="genere-list">
                <Form.Label>Generes:</Form.Label>
                <Form.Text>
                    {generes && generes.join(", ")}
                </Form.Text>
            </Form.Group>
        </div>
    )
}

export default RecordDetails
