// ---------- PlayList---------------------

const ACTIONS = {
    PUSH_TRACK : "Push tack on end of list",
    //"Affter append track to list is fire calculating Bpm, after witch is seting bpm to track on list"
    SET_BPM : "Update Bpm track on playlist",
    SET_BPM_AND_OFFSET : "Update Bpm and Offset track on playlist", // bpm is rounded

    //---play list explorer
    PL_MAKE_DIR : "Create dir in current",
    PL_CREATE_PLAYLIST: "Creawte play list in current selected dir of name",

    PL_SET_SELECTION : "Set current selected dir and play list if is selected play list",
    PL_OPEN_EL: "Expand dir or open play list and set selection to it",
    PL_COLAPSE_DIR : "Colapse dir and set current selection on it",
    PL_DELETE_SELECTED: "Remove current selected element",

}
export {ACTIONS as PLAY_LIST_ACTIONS }
//***------------------- */
export function pushTrackToList(track){
    return {
        type: ACTIONS.PUSH_TRACK,
        track
    }
};

export function setBpm(source, id, bpm){
    return {
        type: ACTIONS.SET_BPM,
        source,
        id,
        bpm,
    }
}

export function setBpmAndOffset(source, id, bpm, offset){
    return {
        type: ACTIONS.SET_BPM_AND_OFFSET,
        source,
        id,
        bpm,
        offset,
    }
}

/**---------------------- */

export function crateDir(name){
    return {
        type: ACTIONS.PL_MAKE_DIR,
        name,
    }
}

export function createPlaylist(name){
    return {
        type: ACTIONS.PL_CREATE_PLAYLIST,
        name,
    }
}

export function setSelection(path){
    return {
        type: ACTIONS.PL_SET_SELECTION,
        path,
    }
}

export function open(path){
    return {
        type: ACTIONS.PL_OPEN_EL,
        path
    }
}

export function colapse(path){
    return {
        type : ACTIONS.PL_COLAPSE_DIR,
        path,
    }
}

export function deleteSelected(path){
    return {
        type: ACTIONS.PL_DELETE_SELECTED
    }
}
