import {ACTIONS} from "./../../actions";

const initState={
    list: [],
    countAll: null,
    currRec: null,

    deleteStatus: "IDLE",
}

export default function recordsReducer(state = initState, action){
    switch(action.type){
        case ACTIONS.RECS_LOAD : {
            const nextState = {
                ...state,
                list : action.records,
            }
            if(action.countAll){
                nextState.countAll = action.countAll;
            }
            return nextState;
        }

        case ACTIONS.RECS_SET_DATA: {
            const nextState = {
                ...state,
                currRec: action.recData,
            }   
            return nextState;
        }

        case ACTIONS.RECS_SET_DELETE_STATUS: {
            const nextState = {
                ...state,
                deleteStatus : action.status,
            }
            return nextState;
        }

        default: return state;
    }
}
