import React from "react";
import KnobDescribed from "./../../../../common/KnobDescribed/KnobDescribed"
import "./eq-knob.scss";


const EqKnob = props =>{
    const toDbValue = (value) => {
        let gain = 1 + value / 100;
        let dB = Math.floor(20 * Math.log10(gain) * 100) / 100 ;
        dB = (dB === -Infinity) ? "-inf" : dB;
        return  dB + "dB";
    }

    return (
        <KnobDescribed className={"eq-knob " + props.className}
            showValue 
            unsymetric={{positive:2}} 
            alt={props.alt}
            displayFormula={ toDbValue }
            quantize={{negative: 1, positive:0.1}} 
            onChange={ props.onChange }
            dobuleClickInit/>
    )
}

export default EqKnob;
