import store from "./../../../store/";
import getApi from "./../../../apis/apiProvider";
import { getBeatLength } from "./../../../utils/bpm/converter";
import { setCuePoint, setChannelReady, setTimeLeft } from "./../../../actions";

export default class Channels {
  constructor() {
    this.dispatch = store.dispatch;
    this.channels = {
      A: null,
      B: null
    };

    this.stdDivceDiff = {};
  }

  getChannelNames() {
    return Object.keys(this.channels);
  }

  setChannel(channelName, player) {
    this.channels[channelName] = player;
  }

  getChannel(channelName) {
    return this.channels[channelName];
  }

  getChannelInterface(channelName) {
    return {
      getSyncBarPosition: () => this.getSyncBarPostion(channelName)
    };
  }

  //--------------------------------------------

  loadTrack(channelName, track) {
    let api = getApi(track.source);
    let url = (api && api.getUrl(track.id)) || null;

    this.dispatch(setChannelReady(false, channelName));

    let player = this.channels[channelName];

    if (player.loadWithEvent) {
      player.loadWithEvent(url);
    } else {
      player.load(url);
    }
  }

  updatePosition(channelName) {
    this.updateTimeLeft(channelName);
  }

  updateTimeLeft(channelName) {
    let channel = this.channels[channelName];
    this.dispatch(
      setTimeLeft(
        channelName,
        parseInt(channel.getDuration() - channel.getCurrentTime())
      )
    );
  }

  togglePlay(channelName, currentValue) {
    if (currentValue) {
      this.channels[channelName].pause();
    } else {
      this.channels[channelName].play();
    }
  }

  toggleCue(channelName, currentValue) {
    const player = this.channels[channelName];
    const isPaused = store.getState().console.channel[channelName]
                    .playBackState.paused;
    if (!currentValue && isPaused) {
      // back to cue point and pause
      let cuePoint = store.getState().console.channel[channelName]
                    .playBackState.cuePoint; //in seconds
      //seek to
      player.pause();
      player.backend.seekTo(cuePoint);
      player.drawer.progress(cuePoint / player.getDuration());
      player.drawer.recenter(cuePoint / player.getDuration());
    } else if (!currentValue) {
      //Cancel cue and play
      if (!player.isPlaying()) {
        player.play();
      }
    } else {
      const cuePoint = this.channels[channelName].getCurrentTime();
      this.channels[channelName].play();
      this.dispatch(setCuePoint(channelName, cuePoint)); // in float seconds
    }
  }

  adjustPitch(channelName, currentValue) {
    this.channels[channelName].setPlaybackRate(1 + currentValue / 100);
  }

  //---------------------

  getSyncBarPostion(channelName) {
    let state = store.getState();
    if (!state.console.master) return null;

    const masterChannel = this.channels[state.console.master];
    const slaveChannel = this.channels[channelName];
    if (!masterChannel.isPlaying() || !slaveChannel.isPlaying()) return null;

    // --- stabilization - beacuase :
    // masterChannel.getCurrentTime() - slaveChannel.getCurrentTime() is not stable as should be
    // cause of this is calculated average diff and diviation from this diff.
    if(!this.stdDivceDiff[channelName]){
        this.stdDivceDiff = {...this.stdDivceDiff, [channelName] : null}
        this.stdDivceDiff[channelName] = masterChannel.backend.ac.currentTime 
                                       - slaveChannel.backend.ac.currentTime;
    }

    const currentDiv = masterChannel.backend.ac.currentTime 
                     - slaveChannel.backend.ac.currentTime;
    this.stdDivceDiff[channelName] = ( this.stdDivceDiff[channelName] + currentDiv ) / 2; //avg
    const deviation = this.stdDivceDiff[channelName] - currentDiv;
    //----------------------

    let masterPosition = masterChannel.getCurrentTime();
    let slavePosition = slaveChannel.getCurrentTime() + deviation;


    const masterBpm = state.console.channel[state.console.master].track.bpm;
    const masterBeatLength = getBeatLength(masterBpm);
    const masterOffset = state.console.channel[state.console.master].playBackState.offset;

    const slaveBpm = state.console.channel[channelName].track.bpm;
    const slaveBeatLength = getBeatLength(slaveBpm);
    const slaveOffset = state.console.channel[channelName].playBackState.offset;

    const masterBeatPosition = (masterPosition - masterOffset) % masterBeatLength ;
    const slaveBeatPosition = (slavePosition - slaveOffset) % slaveBeatLength ;

    const beatDiff = masterBeatPosition - slaveBeatPosition;
    
    //when master track is in the current beat but slave is in the next one 
    //and in invers situation 
    const beatDiffEdgeNegative = masterBeatLength 
                               - masterBeatPosition 
                               + slaveBeatPosition; // slave is overtaking
    const beatDiffEdgePositive = slaveBeatLength 
                               + masterBeatPosition 
                               - slaveBeatPosition ; // slave is delayed
   // console.log("mp", masterBeatPosition);
   // console.log("sl", slaveBeatPosition);
   // console.log(beatDiff, beatDiffEdgeNegative, beatDiffEdgePositive)

    const min = Math.min(Math.abs(beatDiff), 
                         Math.abs(beatDiffEdgeNegative), 
                         Math.abs(beatDiffEdgePositive))

    switch(min){
        case Math.abs(beatDiff): {
            return beatDiff / masterBeatLength;
        }
        case Math.abs(beatDiffEdgeNegative): {
            return - Math.abs(beatDiffEdgeNegative / masterBeatLength);
        }
        case Math.abs(beatDiffEdgePositive):{
            return Math.abs(beatDiffEdgePositive) / masterBeatLength;
        }
        default : return min / masterBeatLength;
    }
  }
}
