import { useMemo, useState, useEffect, useCallback } from "react"
import { getApi } from "./../../../apis/apiProvider";
import { PLAYBACK_STATE } from "./usePlabackState";

//getRecordUrl
class PlayerControler {
    btnStateHandlers = {}
    setProgressHandlers = {}
    getProgressDataHandler = {};
    progressUpdaterHandler = null;
    current = {
        id: null,
        start: 0,
        duration: 0,
        progress: 0,
        state: null,
    };

    setBtnCtrlHandler(id, handler) {
        this.btnStateHandlers[id] = handler;
    }

    setBtnState(id, state) {
        this.btnStateHandlers[id](state)
    }

    setProgressHandler(id, handler) {
        if (this.setProgressHandlers[id]) {
            this.setProgressHandlers[id].push(handler);
            
        } else{
            this.setProgressHandlers[id] = [handler];
        }
    }

    setProgress(id, progress) {
        for (let handler of this.setProgressHandlers[id]) {
            handler(progress)
        }
        this.current.progress = progress;
    }

    setProgressDataHandler(id, handler) {
        this.getProgressDataHandler[id] = handler;
    }

    getProgressData(id) {
        if(!id){
            return this.getProgressData(this.current.id);
        }
        return this.getProgressDataHandler[id]();
    }

}


export function useRecordPlayer() {
    const [mediaElement, setMediaElement] = useState(null);
    // const ctr = useContext(PlayerContext);
    const [ctr, _] = useState(new PlayerControler())

    const api = useMemo(() => {
        return getApi('RecordsStore');
    }, [getApi])

    useEffect(() => {
        const medEl = document.createElement('audio');
        setMediaElement(medEl);
        ctr.mediaElement = medEl;
    }, [setMediaElement, ctr])

    const startUpdateProg = useCallback(() => {
        const handler = setInterval(() => {
            const progress = mediaElement.currentTime / (ctr.current.duration / 1000);
            ctr.setProgress(ctr.current.id, progress);
        }, 200)
        ctr.progressUpdaterHandler = handler;
    }, [mediaElement, ctr])

    const stopUpdateProg = useCallback(() => {
        clearInterval(ctr.progressUpdaterHandler);
    }, [ctr])

    const playback = useCallback((id, pbState) => {
        if (pbState === PLAYBACK_STATE.PLAY) {
            const { progress, duration } = ctr.getProgressData(id);
            const position = (duration / 1000) * progress;
            mediaElement.src = api.getRecordUrl(id);
            mediaElement.currentTime = position;
            mediaElement.play();
            if (ctr.current.id) {
                ctr.setBtnState(ctr.current.id, PLAYBACK_STATE.PAUSE)
            }
            ctr.current = {
                id,
                duration,
                progress: progress,
                state: PLAYBACK_STATE.PLAY,
            }
            startUpdateProg()
        } else if (pbState === PLAYBACK_STATE.PAUSE) {
            mediaElement.pause();
            ctr.current.state = PLAYBACK_STATE.PAUSE;
            stopUpdateProg();
        }
    }, [
        mediaElement,
        api,
        ctr,
        stopUpdateProg,
        startUpdateProg,
    ])

    const seek = useCallback(({ id, progress, duration }) => {
        stopUpdateProg();

        //     const bufferdTimeRanges = mediaElement.buffered;
        //    // const end = bufferdTimeRanges.length && bufferdTimeRanges.end(bufferdTimeRanges.length-1);
        //     if(bufferdTimeRanges.length){
        //         //console.log('seeekable',mediaElement.seekable.end(mediaElement.seekable.length - 1));
        //         console.log(bufferdTimeRanges.end(bufferdTimeRanges.length-1));
        //     }

        if (ctr.current.id && ctr.current.id !== id) {
            ctr.setBtnState(ctr.current.id, PLAYBACK_STATE.PAUSE);
        }
        const position = (duration / 1000) * progress;

        if (ctr.current.id !== id) {
            mediaElement.src = api.getRecordUrl(id);
            mediaElement.currentTime = position;
            ctr.setBtnState(id, PLAYBACK_STATE.PLAY);
            ctr.setProgress(id, progress);
            ctr.current = {
                id,
                duration,
                state: PLAYBACK_STATE.PLAY
            }
        }

        mediaElement.currentTime = position;
        if (mediaElement.pause) {
            mediaElement.play();
        }

        startUpdateProg();

    }, [ctr, api, mediaElement, stopUpdateProg, startUpdateProg])

    return [playback, seek, ctr]
}
