import React, {useRef, useEffect, useState, useCallback, useContext} from "react";
import WaveSurfer from "wavesurfer";
import style from "./wave-surfer-player.scss";
// import {PLAYBACK_STATE} from "./../PlaybackButton/PlaybackButton"
import {PlayerContext} from "./../../useRecordPlayer";

const WaveSurferPlayer = ({item : {peaks, id, duration, fileSize}, seekHandler}) => {
    const containerRef = useRef(null)
    const [container, setContainerRef] = useState(null);
    const [ws, setWs] = useState(null);
    const [progress, setProgress] = useState(0);
    const playerContext = useContext(PlayerContext);

    const onContainerRef = useCallback( (ref) => {
        setContainerRef(ref);
    }, [setContainerRef])

    useEffect(()=>{
        if(container){
            const ws = new Promise((res, rej)=>{
                try{
                    const ws = WaveSurfer.create({
                        container: container,
                        barWidth: 2,
                        height: 100,
                        waveColor: style.waveColor,
                        progressColor: style.progressColor,
                        barMinHeight: 0
                    })
                    res(ws)
                } catch (err){
                    rej(err)
                }
            })
            ws.then( ws => {
                setWs(ws);
                return ws;
            })

            ws.then((ws) =>{
                if(peaks){
                    ws.load(null, peaks);
                }
                if(seekHandler){
                    ws.on('seek', (progress)=>{
                        const filePosition = ~~(parseInt(fileSize)  * progress);
                        seekHandler({
                            id, 
                            filePosition, 
                            progress,
                            duration,
                        })
                        ws.drawer.progress(progress);
                    })
                }
            })
        }
    }, [container, peaks, setWs ])

    useEffect(()=>{
        if(ws && progress){
            ws.drawer.progress(progress);
        }
    },[ws, progress])

    const getProgressData = useCallback(()=>{
        console.log(progress)
        return {
            duration,
            progress,
            filePosition: ~~(parseInt(fileSize)  * progress)
        }
    }, [duration, progress, fileSize])

    useEffect(()=>{
        playerContext.setProgressHandler(id, setProgress)
        playerContext.setProgressDataHandler(id, getProgressData);
    }, [playerContext])

    return (
        <div className="ws-player-container" ref={onContainerRef}/>
    )
}

export default WaveSurferPlayer
