import React, { useCallback, useMemo , useState} from "react"
import PlaybackButton, { PLAYBACK_STATE } from "./PlaybackButton/PlaybackButton";
import GenereList from "./GenereList/GenereList";
import WaveSurrferPlayer from "./WaveSurrferPlayer/WaveSurferPlayer";
import { Link } from "react-router-dom";
import { getApi } from "./../../../../../apis/apiProvider";


const RecordListItem = ({ item , seekHandler, playbackHandler }) => {
    console.log(item)
    // const playBackStateChange = useCallback((state) => {
    //     switch (state) {
    //         case PLAYBACK_STATE.PLAY: {
    //             console.log("Start Playling");
    //             break;
    //         }
    //         case PLAYBACK_STATE.PAUSE: {
    //             console.log("Stop playling");
    //             break;
    //         }
    //     }
    // }, [])

    // const [playback, setPlayback] = useState(PLAYBACK_STATE.PAUSE)

    // const url = useMemo(()=>{
    //     const api = getApi("RecordsStore");
    //     return api.getRecordUrl(item.id);
    // }, [item, getApi])

    const onPlaybackChange = useCallback(()=> {
        if(playbackHandler){
            playbackHandler(item.id);
        }
    }, [playbackHandler, item])

    return (
        <li className="record-list-item">
            <header>
                <nav>
                    <Link to={"/my/records/" + item.title} >
                        <h6 className="record-title">{item.title}</h6>
                    </Link>
                </nav>
                <aside>
                    <GenereList list={item.genere} />
                </aside>
            </header>
            <main>
                <PlaybackButton onChange={onPlaybackChange} />
                <WaveSurrferPlayer peaks={item.peaks} seekHandler={null}/>
            </main>
        </li>
    )
}

export default RecordListItem
