import React, { useCallback, useEffect, useMemo, useState } from "react";
import { Col, Container, Row } from "react-bootstrap";
import { connect } from "react-redux";
import LOG_TYPES from "./../../LoggerLogTypes";
import "./logger.scss";

const Logger = ({ log }) => {
    const [text, setText] = useState();

    useEffect(() => {
        if (!message) {
            return;
        }

        setText(message);
    }, [message])

    const getHeader = useCallback((text, className = "") => {
        return <h6 className={"logger-header " + className}>{text}</h6>
    }, []);
    const header = useMemo(()=>{
        switch(log.type){
            case LOG_TYPES.ERROR : {
                return getHeader("Error:", "logger-header-error");
            }
            case LOG_TYPES.DEBUG: {
                return getHeader("Debug:", "logger-header-debug");
            }
            case LOG_TYPES.LOG : {
                return getHeader("Log");
            }
            default: return null;
        }
    }, [log, getHeader])

    return (
        <Container className="app layout container-xl" >
            <Row><Col>
                <div className="logger">
                    {header}
                    <p className="logger-message">
                        {text}
                    </p>
                </div>
            </Col></Row>
        </Container>
    )
}

const mapStateToProps = state => ({
    log: state.logger.last,
})

export default connect(mapStateToProps)(Logger);
