import React from "react";
import "./knob.scss";



class Knob extends React.Component{

    state = {
        rightArm: 270,
        leftArm: 0,
    }

    render(){
        return (
            <div className="knob">
                <div 
                    className="knob-big-circle"
                    style={{
                        backgroundImage :         
                        `linear-gradient(`+ this.state.leftArm +`deg, transparent 50%, white 50%),
                         linear-gradient(`+ this.state.rightArm + `deg, white 50%, transparent 50%)`
                    }}
                    />
                <div className="knob-small-circle"/>
            </div>
        )
    }
}


export default Knob;
