import STATUS from  "./../STATUS";

const responseTempalte = {
    status : STATUS,
    chanel: "A or B",
    prevValue: 0,
    currentValue: 0,  
}

export default function checkIsPlaying(prev, current){
    let response = null;
    for(let chanelName of Object.keys(prev.chanel)){
        const prevValue = prev.chanel[chanelName].playBackState.paused;
        const currentValue = current.chanel[chanelName].playBackState.paused;
        if( prevValue !== currentValue)
            {
                return {
                    status : STATUS.TOGGLE_PLAY,
                    channel : chanelName,
                    prevValue : prevValue,
                    currentValue : currentValue
                }                
            }
    }
    return response;
}
