import STATUS from "./../STATUS";

export default function recState(prev, current) {
  prev = prev.recorder;
  current = current.recorder;
  let response = [];

  if(prev.recording != current.recording){
        response.push({
            status: STATUS.RECORDER,
            subStatus : (current.recording) ? STATUS.RECORD_START : STATUS.RECORD_END,
            recParam : {
              recName : current.recName,
              recId : current.recId
            }

        })
  }

  return response;
}
