import React from "react";

export default function withMousePosition(WrappedComponent){

  return class extends React.Component {
    handleMousePostion(event){
        console.log(event)
    }

    render() {
      return (
          <div className="with-mouse-wrapper" style={{display:"inherit"}}>
            <WrappedComponent onMouseOver={this.handleMousePostion} {...this.props} />
         </div>
        );
    }
  };
};
