function locateMention(value, fromIndex) {
    return value.indexOf('{.', fromIndex)
}

tokenizeCssClass.notInLink = true
tokenizeCssClass.locator = locateMention

function tokenizeCssClass(eat, value, silent) {
    console.log("value", value)
    var match = /^{\.(\w+)}/.exec(value)

    if (match) {
        if (silent) {
            return true
        }

        //const add = eat(match[0]);

        return eat(match[0])({
            // type: 'htmlClass',
            type:  "cssClass",
            className: match[2],
        })
/*        return add({
             type: 'link',
            // url: 'https://social-network/' + match[1],
            // children: [{type: 'text', value: match[0]}]
            data: {
                className: 'custom-block-heading',
                hProperties: {
                    className: 'custom-block-heading',
                }
            }
        })*/
    }
}

module.exports.tokenizeHtmlClass = tokenizeCssClass;
