import React, { useCallback, useMemo } from 'react';
import UUID from 'uuidjs'
import icons from "./emoji.json";
const iconsOnPanel = 30;

const buttonStyle = {
    background: 'transparent',
    border: 'none',
    boxShadow: 'none',
    outline: 'none'
}

const sectionStyle = {
    width: '100%',
    height: '100%',
}

const containerStyle = {
    overflow : 'hidden',
    display: 'flex',
    width: '200px',
    height: '200px'
}

const EmotiContainer = ({ onSelect, node }) => {


    const iconsPanels = useMemo(() => {
        let panels = [];
        for (let i = 0; i < icons.length; i += iconsOnPanel) {
            console.log(i)
            let panel = [];
            for (let j = i; (j < iconsOnPanel + i) && (i + j < icons.length); j++) {
                let icon = icons[j]
                panel.push(
                    <button key={'icon-'+ (i+j)} style={buttonStyle} onSelect={onSelect.bind(null, icon.emoji)}>{icon.emoji}</button>
                )
            }
            panels.push(
                <section style={sectionStyle} className="emoticons-panel" key={'pannel-'+i}>
                    {panel}
                </section>
            )
        }


        return panels;
    }, [])

    console.log("render emoti")
    return (
        <div className="emoticons-container" style={containerStyle}>
            {iconsPanels}
        </div>
    )
}

export default EmotiContainer;
