import React, { useState, useRef, useCallback } from "react";
import "./menu-bar.scss";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faFolderPlus, faFile } from "@fortawesome/free-solid-svg-icons";
import { Form } from "react-bootstrap";
const MenuBar = props => {
    const [type, setType] = useState(null);
    const elementName = useRef();

    const newElementHandler = useCallback(type => {
        setType(type);
        elementName.current.focus();
    }, [elementName, setType])

    const createElement = useCallback((name) => {
        console.log(name)
    }, [])

    return (
        <div className="menu-bar">
            <h6 className="title">Explorer</h6>
            <Form.Control
                ref={elementName}
                className="file-name-input"
                type="text" size="sm"
                onKeyPress={e => { if (e.key === "Enter") { createElement(this.value) } }}
                onBlur={createElement.bind(null, this.value)}
            />
            <div className="flex-spacer" />
            <button
                className="btn-transparent btn-new-file"
                onClick={newElementHandler("dir")}>
                <FontAwesomeIcon
                    className="icon icon-new-folder"
                    icon={faFolderPlus} />
            </button>
            <button
                className="btn-transparent btn-new-file"
                onClick={newElementHandler("file")}>
                <FontAwesomeIcon
                    className="icon icon-new-file"
                    icon={faFile} />
            </button>
        </div>
    )
}


export default MenuBar;
