import React, {useCallback, useEffect, useRef, useState} from "react";
import UUID from "uuidjs";
import style from "./slider.scss"

 const rmLast = (arr, direction) => {
     let last = arr.length - 1;
     let _arr = [...arr];
     while( last >= 0){
         if(direction === "next" && arr[last] < 0) break;
         if(direction === "prev" && arr[last] > 0) break;
         _arr.pop();
         last--;
     }
     return _arr;
 }

const sumLastSameDirection = arr => {
    let _arr = [...arr];
    if(arr[arr.length - 1] * arr[arr.length - 2] > 0){
        let last = _arr.pop();
        let prev = _arr.pop();
        _arr.push(last + prev);
    }
    return _arr;
}

const Slider = ({slides: initSlides, next:nextHandle, prev: prevHandle, minSlideWidth = style.minSlideWidth}) => {
    const slider = useRef();
    const [_containerRect, setContainerRect] = useState(null);
    const [slideWidth, setSlideWidth] = useState(null);
    const [slides, setSlides] = useState([...initSlides, ...initSlides, ...initSlides]);
    const [isTransitionActive, setTransitionActive] = useState(false);
    const [actionQueue, setActionQueue] = useState([]);
    const shift = useRef();

    const containerRect = useCallback(() => {
        if (!slider.current) return 0;
        let rect = _containerRect
        if (!rect) {
            rect = slider.current.parentElement.getBoundingClientRect()
            setContainerRect(rect);
        }
        return rect;
    }, [_containerRect, setContainerRect])

    const setTransitionClass = useCallback( value =>{
         if(!slider.current) return;
         if(value){
            slider.current.classList.add("slider-transition--active")
            return
         }
         slider.current.classList.remove("slider-transition--active")
    }, [slider])


    useEffect(()=>{
        const transitionEnd = () => setTransitionActive(false);
        window.addEventListener("transitionend", transitionEnd);
        window.addEventListener("mozTransitionEnd", transitionEnd);
        window.addEventListener("webkitTransitionEnd", transitionEnd);
        window.addEventListener("oTransitionEnd", transitionEnd);
        return () => {
            window.removeEventListener("transitionend", transitionEnd);
            window.removeEventListener("mozTransitionEnd", transitionEnd);
            window.removeEventListener("webkitTransitionEnd", transitionEnd);
            window.removeEventListener("oTransitionEnd", transitionEnd);
        }
    }, [setTransitionActive])

    const setPosition = useCallback( position =>{
        if(!slider.current) return;
        slider.current.style.transform = `translateX(${position * slideWidth }px)`
        setTransitionActive(true);
    }, [ slider, slideWidth, setTransitionActive])

    const setTranslate = useCallback( position => {
        if(!slider.current) return;
        slider.current.style.transform = `translateX(${position}px)`
    }, [slider])

    useEffect(()=>{
        shift.current = -initSlides.length;
        setPosition(shift.current);
    }, [slideWidth, setPosition, shift, initSlides])

    useEffect(() => {
        const resetSize = () => setContainerRect(null);
        window.addEventListener("resize", resetSize);
        return () => window.removeEventListener("resize", resetSize);
    }, [setContainerRect])

    useEffect(() => {
        if (!containerRect() || !minSlideWidth || !slider.current) return;
        const containerWidth = containerRect().width;
        const visibleSlides = ~~(containerWidth / parseInt(minSlideWidth));
        const slideWidth = containerWidth/ visibleSlides
        setSlideWidth(slideWidth);
        slider.current.style.width = slideWidth * slider.current.children.length + "px";
    }, [containerRect, minSlideWidth,  setSlideWidth]);

    const appendSlides = useCallback(amount => {
        setSlides( slides => {
            const _slides = [...slides];
            for(let i = 0; i < amount; i++){
                const slide = _slides.shift();
                _slides.push(slide)
            }
            return _slides;
        })
    }, [setSlides])

    const prependSlides = useCallback(amount => {
        setSlides( slides => {
            const _slides = [...slides];
            for(let i = 0; i < amount; i++){
                const slide = _slides.pop();
                _slides.unshift(slide)
            }
            return _slides;
        })
    }, [setSlides])

    const action = useCallback(() => {
        if(!actionQueue.length) return;
        const queue = actionQueue;
        const direction = queue.shift();
        setTransitionClass(false);
        (direction > 0) ? appendSlides(direction) : prependSlides(Math.abs(direction));
        setPosition( shift.current + direction);
        setTimeout(()=>{
            setTransitionClass(true);
            setPosition(shift.current);
            setActionQueue(queue)
        }, 0)
    }, [setTransitionClass, setPosition, actionQueue, setActionQueue])

    useEffect(() =>{
        if(!isTransitionActive){
            action();
        }
    }, [isTransitionActive, actionQueue])

    const next = useCallback(()=>{
        setActionQueue( queue => {
            const _queue = rmLast(queue, "prev");
            return sumLastSameDirection([..._queue, 1])
        })
    }, [setActionQueue])

    useEffect(()=> nextHandle && nextHandle(next), [next, nextHandle])

    const prev = useCallback(()=>{
        setActionQueue( queue => {
            const _queue = rmLast(queue, "next");
            return sumLastSameDirection([..._queue, -1])
        })
    }, [setActionQueue])

    useEffect(()=> prevHandle && prevHandle(prev), [prev, prevHandle])

    const mouseMove = useCallback((shiftX) => {

    }, [])

    const mouseUp = useCallback(()=>{

    }, [])

    const mouseDown = useCallback(event =>{
        const sliderContainer = event.target.closest(".carousel-slider-container");
        const rect = sliderContainer.getBoundingClientRect();
      //  console.log(rect.left, event.);
    }, [])

    console.log(actionQueue)

    return (
        <div className="carousel-slider-container">
            <ul className="carousel-slider" ref={slider} onMouseDown={mouseDown}>
                {slides && slides instanceof Array && slides.map(slide => (
                    <li key={UUID.genV1()} className={"slide"} style={{width: minSlideWidth || "initial"}}>{slide}</li>
                ))}
            </ul>
        </div>
    )
}
export default Slider;
