import { analyze, guess } from "web-audio-beat-detector";

export function calcBpm(url) {
  return fetch(url)
    .then(response => response.arrayBuffer())
    .then(arrayBuffer => {
      const audioCtx = new AudioContext();
      return audioCtx.decodeAudioData(arrayBuffer);
    })
    .then(audioBuffer => analyze(audioBuffer))
    .catch(error => 0);
}

export function calcBpmAndOffset(url) {
  return fetch(url)
    .then(response => response.arrayBuffer())
    .then(arrayBuffer => {
      const audioCtx = new AudioContext();
      return audioCtx.decodeAudioData(arrayBuffer);
    })
    .then(audioBuffer => guess(audioBuffer))
    .catch(error => ({ bpm: 0, offset: 0 }));
}

export function calcAccurateBpmAndOffset(url) {
  return fetch(url)
    .then(response => response.arrayBuffer())
    .then(arrayBuffer => new AudioContext().decodeAudioData(arrayBuffer))
    .then(audioBuffer => {
      console.log("tutaj tak ")
      let bpmAndOffset = guess(audioBuffer);
      console.log("tutaj tez ", guess);
      let accurateBpm =  analyze(audioBuffer);
      console.log(bpmAndOffset, accurateBpm)
      return Promise.all(bpmAndOffset, accurateBpm);
    })
    .then((bpmAndOffset, accBpm) => {
        console.log(guess, accBpm)
      return {
        bpmRounded: bpmAndOffset.bpm,
        offset: bpmAndOffset.offset,
        bpm: accBpm
      };
    })
    .catch(error => ({
      bpm: 0,
      offset: 0,
      bpmRounded: 0, 
      err: error
    }));
}
