//Actions types 
let ACTIONS = {
    LOAD_TRACK : "Load $track to $destination (deck A or B)",
    SET_PITCH : "Settin pitch to $destiantion deck (destinatio A or B)"
}

// ---------Console-------------

export function loadTrack(track, destination ){
    return {
        type: ACTIONS.LOAD_TRACK,
        track: track,
        destination : destination
    }
}

export function setPitch(track, destination){
    return {
        type: ACTIONS.SET_PITCH,
        destination: destination
    }
}

//--------- Serching----------------

ACTIONS = {
    ...ACTIONS,
    SEARCH_INPUT : "Tap in to Search component",
    SET_SEARCH_RESULTS : "Set YT(or oteher source) serch results",
    CLEAR_SERACH : "Clear search string and serach result set"
}

export function searchInput(text){
    return {
        type: ACTIONS.SEARCH_INPUT,
        text: text
    }
}

export function setSearchResults(results){
    return {
        type: ACTIONS.SET_SEARCH_RESULTS,
        results : results,
    }
}

export function clearSearch(){
    return {
        type : ACTIONS.CLEAR_SERACH
    }
}

// ---------- PlayList---------------------

ACTIONS = {
    ...ACTIONS,
    PUSH_TRACK : "Push tack on end of list",
}

export function pushTrackToList(track){
    return {
        type: ACTIONS.PUSH_TRACK,
        track : track
    }
};


export { ACTIONS }
