import React, { useState, useRef, useEffect } from "react";
import WaveSurferElement from "wavesurfer";
import colors from "./../../../css/colors.scss";

const getCssColor= (variable, name) => colors[ "deck" + name.toUpperCase() + variable ]; 

const masterConfig = (container, height, name) => ({ // name deck A or B 
    container: container,
    waveColor: getCssColor('Primary', name) || 'white',
    progressColor: getCssColor('PrimaryDark', name) || 'gray',
    autoCenter : true,
    scrollParent: true,
    hideScrollbar:true,
    fillParent: false,
    height: height,
    minPxPerSec : 150
})

const slaveConfig = (container, height, name) => ({
    container: container,
    waveColor: getCssColor('Primary', name) || 'white',
    progressColor: getCssColor('PrimaryDark', name) || 'gray',
    fillParent: true,
    scrollParent : false,
    hideScrollbar: true,
    scrollParent : false,
    height: height,
})

const config = (type, container, height, name) => {
    switch(type){
        case "master" : return masterConfig(container, height, name);
        case "slave" : return slaveConfig(container, height, name );
        default : return {};
    }
}



const  WaveSurfer = (props) => {

    let [ waveSurfer, setWaveSurfer ] = useState(null);
    let container = useRef(null);
    
    useEffect( ()=> {
        setWaveSurfer(WaveSurferElement.create(config(
            props.type, 
            container.current,
            props.height, 
            props.name,
            )))

    }, []);

    useEffect( () => {
        console.log("wave surfer", waveSurfer)
        if(waveSurfer && props.type === "master" && props.url !== "" ){
            console.log("wave surfer", waveSurfer)
            //waveSurfer.load(props.url);
        }
       
    }, [props.url])

    useEffect( () => {
        console.log("wave surfer", waveSurfer)
        //waveSurfer.load(null, props.peaks);

    }, [props.peaks])


    return (
        <div ref={ container } style={{ height: props.height + "px" }} >  </div>
    )
    
};

export default WaveSurfer;
