import store from "./../../../../../store";
import { recording, recordingRquestFail, recordFinalUpdate } from "./../../../../../actions"
import STATUS from "./../observer/STATUS";
import io from "socket.io-client";
const recorder_api_url = process.env.REACT_APP_RECORDER_API


export default class Recorder {
    static initRecState = () => ({
            id : null,
            title :"",
            peaks : [],
            duration : 0,
    })

    constructor(mixer) {
        this.mixer = mixer;

        const streamDestination = this.mixer.audioNodes.channels['main'].recorderStremDestination;
        this.mediaRecorder = new MediaRecorder(streamDestination.stream, {
            mimeType: 'audio/webm;codecs=opus',
        });

        console.log(recorder_api_url)
        this.currentRec = Recorder.initRecState();
    }



    action(status, param) {
        switch (status) {
            case STATUS.RECORD_START: {
                this.startRecording(param);
                break;
            }
            case STATUS.RECORD_END: {
                this.endRecording()
                break;
            }
        }
    }

    startRecording({recId, recName}) {
        console.log("im start recording", id, recName);
        const token = store.getState().user.token;
        if (!token) {
            return;
        }



        const socket = io.connect(
            recorder_api_url.replace('http', 'ws').replace('https', 'wss'));

        socket.on("connect", () => {
            console.log("we have connection")
            socket.emit('authentication', { token: token });
            socket.on('authenticated', () => {
                console.log('auth')
                socket.emit("record_details", { recId, recName })
            })
            socket.on('recorder_ready', () => {
                this.currentRec.id = recId;
                this.currentRec.title = recName;

                this.mediaRecorder.ondataavailable = (e) => {
                    console.log(e.data)
                    socket.emit('record_chunk', e.data)
                }

                this.mediaRecorder.addEventListener('stop', () => {
                    socket.close()
                })

                this.mediaRecorder.start(1000);
      
                this.currentRec.duration = new Date().getTime();

                this.updateInterval = setInterval(this._updatePeaks.bind(this), 1000);

                store.dispatch(recording());
            })

            socket.on('connect_error', () => {
                store.dispatch(recordingRquestFail())
            })
        })
    }

    endRecording() {
        console.log("im finish recoriding")
        if (this.mediaRecorder.state !== 'inactive') {
            this.mediaRecorder.stop();
            clearInterval(this.updateInterval);

            this.currentRec.duration =new Date().getTime() - this.currentRec.duration;
            this._normalizePeaks();

            const {id, duration, peaks} = this.currentRec;
            store.dispatch(recordFinalUpdate(
                id, peaks, duration
            ))

            this.currentRec = Recorder.initRecState();
        }
    }

    _updatePeaks(){
        this.currentRec.peaks.push(this.mixer.currentMasterPeak)
    }

    //simple interpolation 
    _normalizePeaks(){
        const outputSize = 1000 // max amoutn of peaks
        
        if(this.currentRec.peaks.length > outputSize){
            this.currentRec.peaks = this._interpolate(this.currentRec.peaks , outputSize)
        }
    }

    _interpolate(input, outputSize){
        const inputSize = input.length;
        const output = [];
        const ratio = inputSize/outputSize;
        const range = Math.ceil(ratio) * 2;
        for(let i = 0 ; i < outputSize; i++){
            const from = Math.max(i - range/2, 0);
            const to = Math.min(i + range/2, outputSize - 1)

            let sum = 0;
            for(let j = from; j <= to; j++){
                sum += input[j]
            }
            output[i] = sum / (to - from);
        }
    }
}
