import React from "react";
import {Button} from "react-bootstrap";
import "./looper.scss";

class Looper extends React.Component {
    constructor() {
        this.state = {
            lengths: []
        }

        for(let i = 1 / 64; i < 64 ; i = i * 2){
            this.state.lengths.push(i);
        }
    }


    render(){
        const listItems = this.state.lengths.map( (length, index) =>{
            const display = length;
            if(length < 1){
                display = "1/" + (1/length)
            }
            return (<li key="index" data-value={length}> {display} </li>)
        })

        return(
            <div className={"looper looper-deck-" + this.props.name}>
                <Button>IN</Button>
                <Button>OUT</Button>
                <div className="length-slider">
                    <Button> &lt; </Button>
                        <div className="slider-container">
                            <ul className="slider-list">
                                {listItems.map(item => item)}
                            </ul>
                        </div>
                    <Button> &gt; </Button>
                </div>
            </div>
        )
    }
}
