import {  ACTIONS,  loadRecords, recReqFails } from "../../actions";
import { takeEvery, select, put,  } from "redux-saga/effects";
import { getApi } from "./../../apis/apiProvider";
import { format } from 'date-fns'

export default function* requestUserRecords() {
    yield takeEvery(ACTIONS.RECS_REQ_USER, handel)
}

const getToken = state => state.user.token

//const getCurrentUserId = state => state.user.dbId;

function* handel(action) {
    const token = yield select(getToken);
    console.log('actio load ')
    if(token){
        try{
            const { callQuery , queries } = getApi("UserAssets");

            const query = queries.userRecQl(userId);
            const result = yield callQuery(query, token);

            console.log(result)
            if(!result.errors ){
               // yield put(startRecording(title, id))
            } else {
                throw new Error("Can't load reacords from database")
            }  
        } catch (e) {
            yield put(recReqFails(e.message))
        }

    }

}
