import React, { useContext } from "react"


export default function (ctx, source) {

    const context = ctx;

    console.log(context)



    const dragStartHandler = ()=>{
        console.log("drag start");
        context = {
            ...source,
            dragStart :true,
        }
    }

    return function(WrappedComponet){
        return class extends React.Component{
            constructor(){
                super()
            }

            render(){
                return (
                    <div className="dnd-darg-source" onDragStart={dragStartHandler}>
                        <WrappedComponet {...this.props}/>
                    </div>
                )
            }
        }
    }

}
