import {useEffect, useCallback} from "react"

export default function (){
    useEffect(() => {
        const el = descriptionTextArea.current;
        el.style.height = 0;
        el.style.height = el.scrollHeight + 10 + "px";
    }, [description, descriptionTextArea])

    const updateHeight = useCallback((textarea)=>{
        textarea.style.height = 0;
        textarea.style.height = el.scrollHeight + 10 + "px";
    })

    const onInput = useCallback((event) => {
        if(!event.target ) return;
        if(!(evnet.target instanceof HTMLTextAreaElement)) return;
        updateHeight(event.target)
    }, [updateHeight])

    const addRef = useCallback((textarea)=>{
        updateHeight(textarea)
        textarea.addEventHandler('input', onInput);
        return textarea
    },[updateHeight])

    return [addRef]
}
