import React, {useRef, useEffect, useMemo, useCallback} from "react";
import WaveSurfer from "wavesurfer";
import style from "./wave-surfer-player.scss"

const WaveSurferPlayer = ({peaks, url}) => {
    const containerRef = useRef(null)
    
    const [container, setContainerRef] = useState(null);

    const onContainerRef = useCallback( (ref) => {
        setContainerRef(ref);
    }, [setContainerRef])

    useEffect(()=>{
        if(container){
            ws = new Promise((res, rej)=>{
                try{
                    const ws = WaveSurfer.create({
                        container: container,
                        barWidth: 5,
                        height: 100,
                        waveColor: style.waveColor,
                        progressColor: style.progressColor,
                    })
                    res(ws)
                } catch (err){
                    rej(err)
                }
            })

            ws.then((ws) =>{
                ws.load(null, peaks)
            })
        }
    }, [container, peaks, url])

    // const container = containerRef.current
    // const ws = useMemo(()=>{
