    //           } else rej(null)
    //     })
    // }, [container])

    // useEffect(()=>{
    //     console.log(ws)
    //     ws.then( ws => {
    //         ws.load((null, peaks))
    //     }).catch(()=>console.log("catch it kurwa"))
    // }, [peaks, url ,ws])



    return (
        <div className="ws-player-container" ref={onContainerRef}/>
    )
}

export default WaveSurferPlayer
