import { put, takeEvery } from "redux-saga/effects";
import { ACTIONS, setProfile } from "./../../actions";
import { getApi } from "./../../apis/apiProvider";

export default function* requestUserProfile(){
    yield takeEvery(ACTIONS.P_REQ_PROFILE, handel)
}

function* handel(action){
    try{
        const {callQuery, queries} = getApi("UserAssets");
        const query = queries.getProfileQl;

        const result = yield callQuery(query, null, {nickname: action.nickname})
        console.log(result)
        if(result && !result.errors && result.data){
            yield put(setProfile(result.data.profile))
        }else {
            throw new Error('can \'t get profiele data from database');
        }
    } catch (err) {
        console.log(err.message)
    }
}
