import { setUserProfile } from "./../actions";
import { put , call} from 'redux-saga/effects';
import apiProvider from "./../apis/apiProvider";

export default function *getUserProfi(action){
    const api = apiProvider.getApi("UserApi");
    const query = `
        me {
            email,
        }
    `;
    let result = call(api.callQuery, query);
    put(setUserProfile(result))
}
