export default function useTransitionEnd(element){
    const subscribe = (handle, _element) => {
        const el = _element || element || window;
        el.addEventListener("transitionend", handle);
        el.addEventListener("transitioncancel", handle)
        el.addEventListener("mozTransitionEnd",handle);
        el.addEventListener("webkitTransitionEnd",handle);
        el.addEventListener("oTransitionEnd",handle);
    }

    const unSubscribe = (handle, _element) => {
        const el = _element || element || window;
        el.removeEventListener("transitionend",handle);
        el.removeEventListener("transitioncancel",handle)
        el.removeEventListener("mozTransitionEnd",handle);
        el.removeEventListener("webkitTransitionEnd",handle);
        el.removeEventListener("oTransitionEnd",handle);
    }

    return [subscribe, unSubscribe];
}
