import { ACTIONS } from "./../../actions";

const initState = {
    header: {
        disable: false,
        hidden: false,
        sticki: true,
    },
    footer: {
        type : ["player", "standard"][0]
    }
}

export default function headerState(state = initState, action){
    switch(action.type){

        case ACTIONS.LAYOUT_SET_FOOTER_TYPE : {
            return {
                ...state,
                footer : {
                    ...state.footer,
                    type : action.type,
                }
            }
        }

        default: return state;
    }
}
