import React from 'react';
 import {format, toDate, formatRelative} from "date-fns";


const Created = ({date:timestamp}) =>{
    let timeZoneOffset = new Date().getTimezoneOffset;
    console.log( toDate(timestamp * 1000 - (timeZoneOffset * 1000)))
    return (
        <div className="record-createdAt">
            <span>{formatRelative(toDate(timestamp * 1000 - (timeZoneOffset * 1000)), new Date())}</span>
        </div>
    )
}


export default Created;
