import React from "react";
import BinaryButton from "./../../../common/BinnaryButton/BinnaryButton";
import "./effector-button.scss";
import { connect } from "react-redux";

const EffectorButton = props => {
    const {
        channel,
        effect = null,
        parameter,
        className,
        ...rest
    } = props;


    const mapStateToProps = state => {
        const effect = state.effector.channels[channel].effects[effect];
        return {
            value: (effect) ? effect[parameter] : 0,
        }
    }

    const Button = connect(mapStateToProps)(class extends React.Component {
        render(){
            return (<BinaryButton value={this.props.value} className={'effector-btn ' + className} {...rest}>{props.children}</BinaryButton>)
        }
    });

    return <Button/>
}

export default EffectorButton;
