export function improveWaveSurfer(waveSurfer) {
    const applyStyle = waveSurfer.Drawer.style;
    waveSurfer.drawer._mainWraper = waveSurfer.drawer.container.firstChild;

    const progressLayer = document.createElement('div');
    const style = {
        position : "absolute",
        top : "0",
        left : "0",
        zIndex : "10", 
        width : "1px",
        height: "100%",
        background : "red"
    }
    applyStyle(progressLayer, style);
    progressLayer.id = "wsProgressLayer"
  //  waveSurfer.drawer.wrapper.appendChild(progressLayer);
    waveSurfer.drawer._progressLayer = progressLayer;

    waveSurfer.drawer.progress = function (progress) {
        var minPxDelta = 1 / this.params.pixelRatio;
        var pos = Math.round(progress * this.width) * minPxDelta;

        if (pos < this.lastPos || pos - this.lastPos >= minPxDelta) {
            this.lastPos = pos;

            if (this.params.scrollParent && this.params.autoCenter) {
                var newPos = ~~(this.wrapper.scrollWidth * progress);
                this.recenterOnPosition(newPos);
            }


            this.updateProgress(pos);
        }
    }

    waveSurfer.drawer._updateProgress = function(pos){
        console.log("udpate progress " , pos)
        this._progressLayer.style.transform = "scaleX("+ 2 * pos +")"
    }



    waveSurfer.drawerhandleEvent= function (e, noPrevent) {
        !noPrevent && e.preventDefault();

        var clientX = e.targetTouches ? e.targetTouches[0].clientX : e.clientX;
        var bbox = this.wrapper.getBoundingClientRect();

        var nominalWidth = this.width;
        var parentWidth = this.getWidth();

        var progress;

        if (!this.params.fillParent && nominalWidth < parentWidth) {
            progress = ((clientX - bbox.left) * this.params.pixelRatio / nominalWidth) || 0;

            if (progress > 1) {
                progress = 1;
            }
        } else {
            progress = ((clientX - bbox.left + this.wrapper.scrollLeft) / this.wrapper.scrollWidth) || 0;
        }

        return progress;
    }
}
