import React, { useEffect, useState, useCallback , useRef} from "react";
import Testimonial from "./Testimonial";
import {Container, Row, Col} from "react-bootstrap"
import UUID from "uuidjs";
import "./testimonials.scss";

const getTestimonials = () => require.context('./testimonials', true, /\.json$/).keys();

const Testimonials = () => {
    const [testimonials, setTestimonials] = useState(null);
    const slideWrapper = useRef();
    const slide = useRef();
    
    const testimonialsJsons = useCallback(()=>getTestimonials().map( file => {
        return import("./testimonials" + file.substr(1, file.length));
    }), []);

    useEffect(()=>{
        if(!testimonialsJsons) return;
        Promise.all(testimonialsJsons())
        .then( jsons => jsons.map( module => module.default))
        .then( testimonials => {
            setTestimonials(testimonials)
        })
    }, [testimonialsJsons, setTestimonials])


    const changeSlide = useCallback( direction => {
        if(!slideWrapper.current) return; 
        if(!slide.current) slide.current = 0; 
        
        switch(direction){
            case "next": {  
                slide.current++;
                slideWrapper.current.append(slideWrapper.current.children[slide.current].cloneNode(true))
                break;
            }
            case "prev":{
                slide.current--;
                break;
            }
            default: return null;
        }
        console.log([...slideWrapper.current.children]);

        [...slideWrapper.current.children].forEach(slide => console.log(slide))//slide.style.transform = `translateX(-${slide.current}00%)`);
    }, [slide, slideWrapper])

    if(!testimonials) return null;
    return (
        <section className="home-testmionial">
            <Container>
            <div className="testimonial-slider">
                <Row className="slide-wrapper" ref={slideWrapper}>

                        { testimonials && testimonials.map( tst => 
                            <Col key={UUID.genV1()} lg={4} md={6} sm={12}>
                                <Testimonial data={tst}/>
                            </Col>
                            )
                        }
 
                </Row>
            </div>
            <Row>
                <Col sm={12}>
                    <div className="testimonail-slider-control">
                        <button onClick={changeSlide.bind(null, "prev")}>Prev</button>
                        <button onClick={changeSlide.bind(null, "next")}>Next</button>
                    </div>
                </Col>
            </Row>
            </Container>
        </section>
    )
}

export default Testimonials;
