import React, { useEffect , useRef} from "react";
import { connect } from "react-redux";
import WaveSurfer from "wavesurfer";

class Deck extends React.Component {
    constructor(){
        super();
        this.mainWave = React.createRef();
        this.smallWave = React.createRef();
        
    }


    componentDidMount(){
        if(!this.props.active) return;
        this.bigWaveSurfer = WaveSurfer.create({
            container: this.mainWave.current,
            waveColor: 'violet',
            progressColor: 'purple',
            autoCenter : true,
            scrollParent: true,
            hideScrollbar:true,
            fillParent: false,
            height: 130,
            minPxPerSec : 150
        });

        this.smallWaveSurfer = WaveSurfer.create({
            container: this.smallWave.current,
            height: 50,
        })

        this.bigWaveSurfer.on('ready', () => {
            this.bigWaveSurfer.play();
            let peaks = this.bigWaveSurfer.backend.getPeaks(20000, 0, 20000);
            this.smallWaveSurfer.drawer.drawPeaks(peaks, 20000, 0, 20000);
            console.log()

        });

        this.bigWaveSurfer.load('https://ciagnik-yt-downloader.herokuapp.com/download?url=https://www.youtube.com/watch?v=3nbY4kpCF5w');
    }

    render() {
        return (
            <div style={{height: 250 + 'px'}}>
                {this.props.children}
                <div ref={ this.mainWave } style={{height:70 + '%'}} >  </div>
                <div ref={ this.smallWave }  style={{height:30 + '%'}} >  </div>
            </div>
            )
        }
    
}

export default connect()(Deck);
