import { toggleDir, ACTIONS, loadDir } from "../../actions";
import { takeEvery, select, put } from "redux-saga/effects"
import { getApi } from "./../../apis/apiProvider";
import requestDirContetQuery from "./../../qlQueries/";
import { get } from "lodash/object";

export default function* createDirRequestSaga() {
    yield takeEvery(ACTIONS.PL_TOGGLE_DIR_REQUEST, callApi)
}

const getToken = state => state.user.token;

const getCurrentSelection = state => state.playList.currentSelection;

const getCurrent = (state, path) => get(state.playList, path);

function* callApi(action) {
    const { callQuery } = getApi("UserAssets");
    const token = yield select(getToken);
    const currentSelection = yield select(getCurrentSelection);
    const current = yield select(getCurrent, currentSelection);
    if(current._open || current._loaded){
        //close
        return yield put(toggleDir, action.path )
    } else {
        let dirContent = callQuery(requestDirContetQuery(current.id), token).data.dir;
        yield put(loadDir, action.path, dirContent)
    }
