import React, { useCallback, useState, useRef, Fragment } from "react";
import { connect } from "react-redux";
import ExplorerContextMenu from "./../../../common/ContextMenu/ContextMenu";
import { ContextMenuTrigger } from "react-contextmenu";

import { 
    toggleDir as toggleDirAction, 
    setSelection, 
    openCurrentPlayList, 
    renameSelected, 
    deleteSelected 
} from "./../../../../../../actions"
import { isEmpty, sortObj } from "./../../../../../../utils/objects/helpers";
import UUID from "uuidjs";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import ClassName from "classnames";
import { 
    faFolder, 
    faFile, 
    faAngleRight, 
    faAngleDown 
} from "@fortawesome/free-solid-svg-icons";
import { useDoubleClick } from "./useDoubleClick";
import "./explorer-tree.scss";

const ExplorerTree = props => {
    const setSelectionHandler = props.setSelection;
    const [renameMode, setRenameMode] = useState(false);
    const renameInputRef = useRef();


    const propRenameSelected = props.renameSelected
    const rename = useCallback(() => {
        propRenameSelected(renameInputRef.current.value)
        setRenameMode(false);
    }, [setRenameMode, propRenameSelected])


    const renameInput = useCallback((name) => {
        return (
            <input
                type="text"
                className="rename-input"
                defaultValue={name}
                ref={renameInputRef}
                onBlur={rename.bind(null)}
                onKeyPress={event => { if (event.key === "Enter") rename() }}
                autoFocus
            />
        )
    },[rename])

    const toggleDirHandler = props.toggleDir;
    const [dirClickHandler] = useDoubleClick({
        clickHandler: props.setSelection,
        doubleClickHandler: toggleDirHandler
    })


    const getDirElement = useCallback((name, path, open, empty) => {
        let currentSelected = false;
        if ([...path, name].join("") === props.currentSelection.join("")) {
            currentSelected = true;
        }
        const liClassList = ClassName(
            "list-item item-dir",
            { "item-dir--empty": empty },
            { "item--selected": currentSelected }
        )
        let content = name;
        if (currentSelected && renameMode) {
            content = renameInput(name)
        }
        return (
            <li
                onClick={((!renameMode) ? dirClickHandler.bind(null, [...path, name]) : null)}
                onContextMenu={setSelectionHandler.bind(null, [...path, name])}
                className={liClassList}
                data-path={path.join("/")}
                key={UUID.genV1()}>
                {!empty &&
                    <button
                        className={"btn-dir " + ((open) ? "btn-dir--expand" : "btn-dir--colapse")}>
                        <FontAwesomeIcon
                            className="icon"
                            icon={(open) ? faAngleDown : faAngleRight}
                            onClick={toggleDirHandler.bind(null, [...path, name])} />
                    </button>}
                <FontAwesomeIcon className="icon icon-dir" icon={faFolder} />
                {content}
            </li>
        )
    }, [dirClickHandler, setSelectionHandler])

    const [fileClickHandler] = useDoubleClick({
        clickHandler: props.setSelection,
        doubleClickHandler: props.openSelected
    })

    const getFileElement = useCallback((name, path) => {
        let currentSelected = false;
        if ([...path, name].join("") === props.currentSelection.join("")) {
            currentSelected = true;
        }
        const liClassList = ClassName(
            "list-item item-file",
            { "item--selected": currentSelected }
        )
        let content = name;
        if (currentSelected && renameMode) {
            content = renameInput(name)
        }
        return (
            <li
                className={liClassList}
                onClick={((!renameMode) ? fileClickHandler.bind(null, [...path, name]) : null)}
                onContextMenu={props.setSelection.bind(null, [...path, name])}
                data-path={path.join("/")}
                key={UUID.genV1()}>
                <FontAwesomeIcon className="icon icon-file" icon={faFile} />
                {content}
            </li>
        );
    })

    const renderDirElements = useCallback((name, content, path) => {
        content = sortObj(content);
        if (content instanceof Array) {
            return getFileElement(name, path)
        } else {
            if (content._open === true) {
                return (
                    <Fragment key={UUID.genV1()}>
                        {getDirElement(name, path, true)}
                        {renderDir(content, [...path, name])}
                    </Fragment>
                )
            } else {
                if (isEmpty(content)) {
                    return getDirElement(name, path, false, true)
                }
                return getDirElement(name, path, false)
            }
        }
    })


    const renderDir = useCallback((dir, path) => {
        return (
            <ul className="list-dir dir-content" key={UUID.generate()}>
                {
                    Object.entries(dir).map(([name, content]) => {
                        if (name.startsWith("_")) {
                            return null;
                        }
                        return renderDirElements(name, content, path);
                    })
                }
            </ul>
        )
    })

    return (
        <Fragment>
            <ContextMenuTrigger id="explorer_context_menu">
                <div className="explorer-tree">
                    {renderDir(props.root, ['root'])}
                </div>
            </ContextMenuTrigger>

            <ExplorerContextMenu
                id="explorer_context_menu"
                items={{
                    "Rename": setRenameMode.bind(null, true),
                    "Delete": props.deleteSelected.bind(null),
                }} />
        </Fragment>
    )
}

const mapStateToProps = state => ({
    root: state.playList.root,
    currentSelection: state.playList.currentSelection
})

const mapDispatchToProps = dispatch => ({
    toggleDir: path => dispatch(toggleDirAction(path)),
    setSelection: path => dispatch(setSelection(path)),
    openSelected: path => dispatch(openCurrentPlayList(path)),
    renameSelected: newName => dispatch(renameSelected(newName)),
    deleteSelected: () => dispatch(deleteSelected())
})

export default connect(mapStateToProps, mapDispatchToProps)(ExplorerTree);
