import React, { useRef, useEffect } from "react"
import { formater } from "./../../../../../../../utils/time/timeFromater";
import { connect } from "react-redux";


const Thumbnail = props => {
    const container = useRef(null);

    useEffect(()=>{
        if(props.thumbnailURL){
            container.src = props.thumbnailURL;
        }

    }, [ container, props.thumbnailURL])

    return (
        <div className="track-info-thumbnail">
            <img alt="track thumbnails" ref={container}/>
        </div>
    )
}

const mapsStateToProps = (state, ownProps) => ({
    thumbnailURL : state.console.channel[ownProps.name]?.track?.thumbnail?.default?.url,
})

export default connect(mapsStateToProps)(Thumbnail);
