export default () => {
    self.addEventListener("message", e => {/* eslint-disable-line no-restricted-globals */
      if (!e) {
          return;
      }
      switch(e.data.type){
          case "BUILD_IMPULSE" : {
             _buildImpulse(
                 e.data.length,
                 e.data.impulseL,
                 e.data.secondsR,
                 e.data.decay,
                 e.data.reverse
             )
          }
      }
    });
    const context = null;

    function _buildImpulse(length, impulseL, impulseR, decay, reverse) {
        let n, i;

        for (i = 0; i < length; i++) {
            n = reverse ? length - i : i;
            impulseL[i] = (Math.random() * 2 - 1) * Math.pow(1 - n / length, decay);
            impulseR[i] = (Math.random() * 2 - 1) * Math.pow(1 - n / length, decay);
        }

        postMessage({type:"IMPLUSE", impulseL , impulseR})
        //this.input.buffer = impulse;
    }

  };
