import STATUS from "./../STATUS";
import STATES from "./../../../../../../reducers/console/recorder/stateDef";

export default function recState(prev, current) {
  prev = prev.recorder;
  current = current.recorder;
  let response = [];

  if(prev.recordingInitiated != current.recordingInitiated){
        response.push({
            status: STATUS.RECORDER,
            subStatus : (current.recordingInitiated) ? STATUS.RECORD_START : STATUS.RECORD_END,
            recParam : {
              recName : current.recName,
              recId : current.recId
            }

        })
  }

  return response;
}
