import React, { useState , useEffect, useRef} from "react";
import "./pitch-control.scss";

const PitchControl= props =>{

    const [sliderRange, setSliderRange] = useState(null);
    const [sliderCurrnetPosition, setSliderCurrentPosition ] = useState(null);
    const [sliderPrevPosition, setSliderPrevPosition ] = userState(null);
    const sliderRangeElement = useRef();
    const sliderThumbElement = useRef();
    const sliderAreaElement = useRef();


    const mouseDownHandle = event =>{
        if(event.target === sliderAreaElement.current || event.target === sliderRangeElement.current){ // area clicked
            let clickY = event.clientY - sliderAreaElement.current.getBoundingClientRect().top;
            setSliderPrevPosition(sliderCurrnetPosition);
            setSliderCurrentPosition(clickY)
        } else if(event.target === sliderThumbElement.current){ // thumb clicked
            console.log('clicked thumb');
        }
    }

    useEffect(() => {
        setSliderCurrentPosition(window.getComputedStyle(sliderThumbElement.current).top);
        setSliderRange(window.getComputedStyle(sliderRangeElement.current).height);

        sliderAreaElement.current.addEventListener('mousedown', mouseDownHandle);
    }, [])

    return (
        <div className={"pitch-control " + "pitch-" + props.name}>
            <div ref={sliderAreaElement} className="pitch-area">
                <div ref={sliderRangeElement} className="slider-range"/>
                <div ref={sliderThumbElement} style={sliderCurrnetPosition && {top: sliderCurrnetPosition}}  className="slider-thumb"/>
            </div>
        </div>
    )
}

export default PitchControl;
