module.exports = parseCssClass;

function parseCssClass() {
   // console.log("this from pars class", this)
    let processor = this;
    let Parser = this.Parser
    let inlineTokenizers = Parser.prototype.inlineTokenizers
    let blockTokenizers = Parser.prototype.blockTokenizers;
    let inlineMethods = Parser.prototype.inlineMethods
    let blockMethods = Parser.prototype.blockMethods;

    console.log('inline methods', inlineMethods);
   // console.log('block methods', blockMethods)
    // Add an inline tokenizer (defined in the following example).


    function locateMention(value, fromIndex) {
        return value.indexOf('{.', fromIndex)
    }

    tokenizeCssClass.notInLink = true
    tokenizeCssClass.locator = locateMention

    function tokenizeCssClass(eat, value, silent) {
     //   console.log("in value", value)
        //var match = /^.+?{\.(\w+)}/.exec(value)
        let match = /^[^(\\*_{}\[\]()#+-.!|]*{\.(\w+)}[^(\\*_{}\[\]()#+-.!|]*/.exec(value)
//        console.log(match);
        if (match) {
            if (silent) {
                return true
            }
            console.log("match in inline", match)
            let rest = match[0].replace("{." + match[1] + "}", "")
            let sub = processor.parse(rest);
            let node = sub.children[0];
            node.className = match[1];
            console.log("returnig from inline", node)
            return eat(match[0])(node)
        }
    }


    function tokenizeCssClassBlock(eat, value, silent) {
      //  console.log("Block value", value)
        var match = /(^[(\\*_{}\[\]()#+-.!|]+){\.(\w+)}((?:[^(\\_{}\[\]()#+-.!|]|\*{1,2})+)/.exec(value)


        if (match) {

            if (silent) {
                return true
            }
            console.log("match in block", match)
         //   let rest = match[0].replace("{." + match[2] + "}", "")
            let sub = this.tokenizeInline(match[1] + match[3]);
            let node = sub.children[0];
            node.className = match[2];

            delete node.position;
            console.log("returnig from block", node);
            return eat(match[0])(node)
        }
    }

    inlineTokenizers.cssClass = tokenizeCssClass;
    blockTokenizers.cssClassBlock = tokenizeCssClassBlock;


    inlineMethods.splice(inlineMethods.indexOf('text'), 0, 'cssClass')
 //   blockMethods.splice(blockMethods.indexOf('paragraph'), 0, 'cssClassBlock')
    blockMethods.unshift('cssClassBlock')
    //blockMethods.splice(blockMethods.indexOf('heading'), 0, 'cssClassBlock')

   // console.log('inline methods', inlineMethods);
}
