const ACTIONS = {
    R_CREATE_RECORDING_REQEST : "Create recording metada",
    R_START_RECORDING : "Start recording action",

    R_END_RECORDING: "Stop recording",
}
export {ACTIONS as RECORDER_ACTIONS};

export function createRecordinRequest(recName){
    return { type: ACTIONS.R_CREATE_RECORDING_REQEST, recName }
}

export function startRecording(recName, id){
    return { type: ACTIONS.R_START_RECORDING, recName, id }
}

export function endRecording(){
    return { type: ACTIONS.R_END_RECORDING}
}
