import React, {useState, useEffect} from "react";
import { formater } from "./../../../../../../utils/time/timeFromater";
import "./time.scss"

const Time = ({ item: { duration, id }, player: playerBus }) => {
    const [progress, setProgress] = useState(0)
    const [current, setCurrent] = useState(0)

    useEffect(() => {
        if (!playerBus) {
            return;
        }
        playerBus.addProgressWatcher(id, setProgress)
    }, [playerBus])

    useEffect(() => {
        setCurrent(duration * progress)
    }, [progress, setCurrent])


    return (
        <div className="time">
            <div className="time-current">
                <span>
                    { formater.secondsToStr(current / 1000) }
                </span>
            </div>
            <div className="time-duration">
                <span>
                    { formater.secondsToStr(duration / 1000) }
                </span>
            </div>
        </div>
    )

}

export default Time;
